mapper.select:
select t.*,t.title name,(select t2.title from t_small_category t2 where t2.id=t.parent_id) parentName from t_small_category t


and t.id = #{id}


and t.title = #{title}


and t.parent_id = #{parent_id}


and t.pic_url = #{pic_url}


and t.level = #{level}


and t.status = #{status}



 and t.create_time  >=  #{startTime}  


 and t.create_time  <=  #{endTime}  


and ${sql_filter}


order by t.create_time desc

select t.id,t.title,t.parent_id,t.status,t.sort_num,t.title name,t.pic_url,(select t2.title from t_small_category t2 where t2.id=t.parent_id) parentName from t_small_category t


     t.id = #{id}


     and t.create_time  >=  #{startTime}  


     and t.create_time  <=  #{endTime}  



order by t.create_time desc
