mapper.select:
select t.*,s.shop_name as "shop.shopName", CONCAT(b.bank_name,'/',b.sub_branch) as "bank.bankName",b.card_no as "bank.cardNo",b.user_name as "bank.userName",b.mobile as "bank.mobile"
from t_small_waste_record t
left join shop s on t.shop_id =s.id
left join shop_bank b on t.bank_id = b.id

       t.shop_id=s.id

           and t.id = #{id}


           and t.shop_id = #{shopId}


           and t.pay_type = #{payType}


           and t.waste_flag = #{wasteFlag}


           and t.amount = #{amount}


           and t.create_time = #{createTime}


           and t.approve_flag = #{approveFlag}


           and t.waste_state = #{wasteState}


           and t.transaction_id = #{transactionId}


           and t.order_no = #{orderNo}


           and t.msg = #{msg}


           and t.approve_time = #{approveTime}


           and t.modify_time = #{modifyTime}


           and t.approve_by = #{approveBy}


           and s.shop_name like CONCAT('%', '${shopName}', '%')


           and b.bank_name like CONCAT('%', '${bankName}', '%')


           and b.sub_branch like CONCAT('%', '${subBranch}', '%')


           and b.card_no like CONCAT('%', '${cardNo}', '%')


           and b.user_name like CONCAT('%', '${userName}', '%')


           and b.mobile like CONCAT('%', '${mobile}', '%')


            and t.create_time  >=  #{startTime}  


            and t.create_time  <=  #{endTime}  


           and ${sql_filter}


order by t.create_time desc,approve_time desc

    select t.*,s.shop_name as "shop.shopName" from t_small_waste_record t,shop s

